package com.lovesickness.o2o.dao;import com.lovesickness.o2o.entity.ProductCategory;import org.apache.ibatis.annotations.Param;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface ProductCategoryDao {    /***     * 根据shopid查询列表     * @return     */    List<ProductCategory> queryProductCategoryByShopId(long ShopId);    /***     * 批量新增店铺商品类别     * @param productCategorieList     * @return     */    int batchSaveProductCategory(List<ProductCategory> productCategorieList);    /***     * 删除指定商品类别     * @param productCategoryId     * @return     */    int deleteProductCategory(@Param("productCategoryId") long productCategoryId, @Param("shopId") long shopId);}