mapper.select:
SELECT o.order_id, o.order_number, o.pay_price, o.is_pay, o.pay_time, o.is_receipt, o.receipt_time, o.is_ship, o.ship_time, o.update_time order_update_time, o.status order_status, o.create_time order_create_time, o.receive_phone, o.receive_addr, o.receive_name, o.track_number, op.order_product_id, p.product_id, p.product_name, p.img_addr FROM tb_product p, tb_order o LEFT JOIN tb_order_product_map op ON o.order_id = op.order_id
p.product_id = op.product_id
AND o.user_id = #{orderCondition.user.userId}
AND o.is_pay=#{orderCondition.isPay}
AND o.is_receipt=#{orderCondition.isReceipt}
AND o.is_ship=#{orderCondition.isShip}
ORDER BY o.create_time DESC LIMIT #{rowIndex},#{pageSize};

SELECT
COUNT(1)
FROM tb_order o


        AND o.user_id = #{orderCondition.user.userId}


        AND o.is_pay=#{orderCondition.isPay}


        AND o.is_receipt=#{orderCondition.isReceipt}


        AND o.is_ship=#{orderCondition.isShip}
