mapper.select:
SELECT o.order_id, o.order_number, o.pay_price, o.is_pay, o.pay_time, o.is_receipt, o.receipt_time, o.is_ship, o.ship_time, o.update_time order_update_time, s.shop_id, s.shop_name, s.shop_desc, s.shop_img FROM tb_shop s, tb_order o LEFT JOIN tb_order_product_map op ON o.order_id = op.order_id
o.shop_id = s.shop_id
AND o.user_id = #{orderCondition.user.userId}
AND o.shopId = #{orderCondition.shop.shopId}
AND p.product_name LIKE '%${keyWord}%'
AND o.is_pay=#{orderCondition.isPay}
AND o.is_receipt=#{orderCondition.isReceipt}
AND o.is_ship=#{orderCondition.isShip}
ORDER BY o.update_time DESC LIMIT #{rowIndex},#{pageSize};

SELECT
COUNT(DISTINCT o.order_id)
FROM tb_shop s,
tb_order o
LEFT JOIN
tb_order_product_map op
ON o.order_id = op.order_id

    o.shop_id = s.shop_id

        AND o.user_id = #{orderCondition.user.userId}


        AND o.shopId = #{orderCondition.shop.shopId}


        AND p.product_name LIKE '%${keyWord}%'


        AND o.is_pay=#{orderCondition.isPay}


        AND o.is_receipt=#{orderCondition.isReceipt}


        AND o.is_ship=#{orderCondition.isShip}

SELECT o.order_id, o.order_number, o.pay_price, o.is_pay, o.pay_time, o.is_receipt, o.receipt_time, o.is_ship, o.ship_time, o.update_time order_update_time, s.shop_id, s.shop_name, s.shop_desc, s.shop_img p.product_id, p.product_name FROM tb_shop s, tb_product p, tb_order o LEFT JOIN tb_order_product_map op ON o.order_id = op.order_id WHERE o.shop_id = s.shop_id
AND p.product_name LIKE '%${keyWord}%'
AND o.shop_id = #{shopId}
AND o.user_id = #{userId}
AND o.is_pay = '1' AND o.is_receipt = '1' AND o.is_ship = '1' AND op.is_evaluation = '0' ORDER BY o.update_time DESC LIMIT #{rowIndex}, #{pageSize};

SELECT COUNT(DISTINCT o.order_id) FROM tb_product p, tb_shop, tb_order o LEFT JOIN tb_order_product_map op ON o.order_id = op.order_id WHERE p.product_id = op.product_id
AND p.product_name LIKE '%${keyWord}%'
AND o.shop_id = #{shopId}
AND o.user_id = #{userId}
AND o.is_pay = '1' AND o.is_receipt = '1' AND o.is_ship = '1' AND op.is_evaluation = '0'

mapper.update:
UPDATE tb_order
is_pay=#{isPay}, pay_time=#{payTime}, is_ship=#{isShip}, ship_time=#{shipTime}, is_receipt=#{isReceipt}, receipt_time=#{receiptTime}, update_time=#{updateTime}, receive_phone=#{receivePhone}, receive_addr=#{receiveAddr}, receive_name=#{receiveName}, track_number=#{trackNumber}, status=#{status}
WHERE order_id = #{orderId} AND user_id = #{user.userId}
