mapper.update:
UPDATE tb_shop
shop_name=#{shopName}, shop_desc=#{shopDesc}, shop_addr=#{shopAddr}, phone=#{phone}, shop_img=#{shopImg}, priority=#{priority}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}, advice=#{advice}, area_id=#{area.areaId}, shop_category_id=#{shopCategory.shopCategoryId}
WHERE shop_id=#{shopId}

mapper.select:
SELECT s.shop_id, s.shop_name, s.shop_desc, s.shop_addr, s.phone, s.shop_img, s.priority, s.create_time, s.last_edit_time, s.enable_status, s.advice, a.area_id, a.area_name, sc.shop_category_id, sc.shop_category_name, p.user_id, p.name FROM tb_shop s, tb_area a, tb_shop_category sc, tb_person_info p
AND s.shop_category_id = #{shopCondition.shopCategory.shopCategoryId}
and s.shop_category_id in (select shop_category_id from tb_shop_category where parent_id = #{shopCondition.shopCategory.parent.shopCategoryId} )
AND s.area_id = #{shopCondition.area.areaId}
AND s.owner_id = #{shopCondition.owner.userId}
AND s.shop_name like '%${shopCondition.shopName}%'
AND s.enable_status = #{shopCondition.enableStatus}
AND s.area_id=a.area_id AND s.shop_category_id=sc.shop_category_id AND s.owner_id=p.user_id
ORDER BY s.priority DESC LIMIT #{rowIndex},#{pageSize};

SELECT COUNT(1) FROM tb_shop s, tb_area a, tb_shop_category sc, tb_person_info p
AND s.shop_category_id = #{shopCondition.shopCategory.shopCategoryId}
and s.shop_category_id in (select shop_category_id from tb_shop_category where parent_id = #{shopCondition.shopCategory.parent.shopCategoryId})
AND s.area_id = #{shopCondition.area.areaId}
AND s.owner_id = #{shopCondition.owner.userId}
AND s.shop_name like '%${shopCondition.shopName}%'
AND s.enable_status = #{shopCondition.enableStatus}
AND s.area_id=a.area_id AND s.shop_category_id=sc.shop_category_id AND s.owner_id=p.user_id
