mapper.select:
SELECT uam.user_award_id, uam.create_time, uam.used_status, uam.point, buyer.user_id buyer_id, buyer.name buyer_name, s.shop_id, s.shop_name, a.award_id, a.award_name, a.award_img, operator.user_id operator_id, operator.name operator_name FROM tb_person_info buyer, tb_shop s, tb_award a, tb_user_award_map uam LEFT JOIN tb_person_info operator ON uam.operator_id = operator.user_id
uam.user_id = buyer.user_id AND uam.shop_id = s.shop_id AND uam.award_id = a.award_id
and uam.user_id = #{userAwardCondition.user.userId}
and uam.shop_id = #{userAwardCondition.shop.shopId}
and buyer.name like '%${userAwardCondition.user.name}%'
and a.award_name like '%${userAwardCondition.award.awardName}%'
and uam.used_status = #{userAwardCondition.usedStatus}
ORDER BY uam.create_time DESC LIMIT #{rowIndex},#{pageSize};

SELECT count(1) FROM tb_person_info buyer, tb_shop s, tb_award a, tb_user_award_map uam LEFT JOIN tb_person_info operator ON uam.operator_id = operator.user_id
uam.user_id = buyer.user_id AND uam.shop_id = s.shop_id AND uam.award_id = a.award_id
and uam.user_id = #{userAwardCondition.user.userId}
and uam.shop_id = #{userAwardCondition.shop.shopId}
and buyer.name like '%${userAwardCondition.user.name}%'
and a.award_name like '%${userAwardCondition.award.awardName}%'
and uam.used_status = #{userAwardCondition.usedStatus}

mapper.update:
UPDATE
    tb_user_award_map
SET
    used_status = #{usedStatus}
WHERE
    user_award_id = #{userAwardId}
    AND user_id = #{user.userId}
