mapper.select:
SELECT upm.user_product_id, upm.create_time, upm.point, s.shop_id, s.shop_name, buyer.user_id, buyer.name, p.product_id, p.product_name, operator.user_id AS operator_id, operator.name AS operator_name FROM tb_user_product_map upm, tb_shop s , tb_person_info buyer, tb_person_info operator, tb_product p
upm.shop_id = s.shop_id AND upm.user_id = buyer.user_id AND upm.product_id = p.product_id AND upm.operator_id = operator.user_id
and upm.user_id = #{userProductCondition.user.userId}
and upm.shop_id = #{userProductCondition.shop.shopId}
and upm.user_name like '%${userProductCondition.user.userName}%'
and p.product_name like '%${userProductCondition.product.productName}%'
and upm.create_time > #{userProductCondition.createTime}
ORDER BY upm.create_time DESC LIMIT #{rowIndex},#{pageSize};

SELECT count(1) FROM tb_user_product_map upm, tb_shop s , tb_person_info buyer, tb_person_info operator, tb_product p
upm.shop_id = s.shop_id AND upm.user_id = buyer.user_id AND upm.product_id = p.product_id AND upm.operator_id = operator.user_id
and upm.user_id = #{userProductCondition.user.userId}
and upm.shop_id = #{userProductCondition.shop.shopId}
and upm.user_name like '%${userProductCondition.user.userName}%'
and p.product_name like '%${userProductCondition.product.productName}%'
and upm.create_time > #{userProductCondition.createTime}
